package com.demo;

public class sampleTest {
	public static void main(String[]args)
	 {
	  String key_s = "";
	  byte[] key_b = hexStringToByteArray(key_s);
	  System.out.println("Round the loop conversion works!byte[] key_b..."+ key_b);
	  String key_s_round = byteArrayToHexString(key_b);
	  System.out.println("Round the loop conversion works!key_s_round...."+ key_s_round);
	  
	  if(key_s.equals(key_s_round))
	   System.out.println("Round the loop conversion works!");
	  else
	   System.out.println("Sorry! still more to make it work");
	 }
	 
	  //Converting a string of hex character to bytes
	  public static byte[] hexStringToByteArray(String s) {
	   int len = s.length();
	   byte[] data = new byte[len / 2];
	   for (int i = 0; i < len; i += 2){
	   data[i / 2] = (byte) ((Character.digit(s.charAt(i), 16) << 4)
	   + Character.digit(s.charAt(i+1), 16));
	   }
	   return data;
	  }
	  
	  //Converting a bytes array to string of hex character
	  public static String byteArrayToHexString(byte[] b) {
	   int len = b.length;
	   String data = new String();

	   for (int i = 0; i < len; i++){
	   data += Integer.toHexString((b[i] >> 4) & 0xf);
	   data += Integer.toHexString(b[i] & 0xf);
	   }
	   return data;
	  }
	  
	 
}
